window.addEventListener("DOMContentLoaded",(()=>{const e=document.querySelector(".search-button"),d=document.querySelector(".modal_nav"),t=document.querySelector(".search_input");e.addEventListener("click",(()=>{d.classList.toggle("hidden_modal")})),window.addEventListener("click",(e=>{e.target===d&&d.classList.add("hidden_modal")})),t.addEventListener("keydown",(e=>{13==e.keyCode&&(window.location.href="/?s="+t.value)}))}));